var expect = require('chai').expect;

function normalize(expr) {
    return expr
        .match(/(?:[0-9a-zA-Z]+|\+|\*|\-|\/|\(|\)|&&|<=>|\|\||==|=>|<=|!=|\!|~|>|<|\^)/g)
        .join(" ");
}

describe('#Helpers', function() {
  it('should normalize given expression #1(simple)', function() {
    expect(normalize('(1&&123)')).to.eql('( 1 && 123 )');
  });

  it('should normalize given expression #2(complex)', function() {
    expect(normalize('4&&2=>3&&t')).to.eql('4 && 2 => 3 && t')
  });

  it('should normalize given expression #3(complex)', function() {
    expect(normalize('4&&2=>3&&t&&t<=>f==1234')).to.eql('4 && 2 => 3 && t && t <=> f == 1234');
  });

  it('should normalize given expression #4(complex)', function() {
    expect(normalize('~1&&t=>f||3&&4')).to.eql('~ 1 && t => f || 3 && 4');
  });

  it('should normalize given expression #5(complex)', function() {
    expect(normalize('( ( A * B ) + ( C / D ) )')).to.eql('( ( A * B ) + ( C / D ) )');
  });

});